INSERT INTO `department_doctor` VALUES ('53', '2017/12/30 13:36:57', '主任医师，绍兴市人民医院肝胆外科副主任，病区主任，绍兴市中西医结合外科学分会副主任委员，绍兴市医师协会外科学分会委员。<br>　　专业特长：肝胆胰脾良恶性疾病的微创治疗，擅长腹腔镜下肝脏切除，腹腔镜胰体尾切除，腹腔镜脾脏切除，腹腔镜胆囊切除，胆总管探查，一期缝合术。', '2018/1/13 15:50:13', '本科', '33060033060247101420170000242', '242', '0', '', '孙学征', '51', '', ',3,', 'face_242.jpg', '', '肝癌和胰腺癌的综合治疗。', '', '0', '93', '', '2', '', '', '主任医师', '1');
INSERT INTO `department_doctor` VALUES ('54', '2017/12/30 13:37:53', '2003年毕业于浙江大学医学院，2011年获浙江大学硕士学位。2010年于浙江大学医学院邵逸夫医院普外科进修腹腔镜技术。<br>　　专业特长：肝胆胰脾良、恶性疾病的诊治和微创治疗。', '2018/1/13 15:51:10', '本科', '33060033060247101420170000243', '243', '0', '', '萧岗', '50', '', ',7,8,', 'face_243.jpg', '', '肝胆胰脾疾病的微创诊治和快速康复。', '发表中华级论文一篇，省级论文一篇。参与多项省市级课题研究。', '0', '93', '', '2', '', '', '副主任医师', '1');
