INSERT INTO `department_doctor` VALUES ('76', '2017/12/30 13:56:04', '毕业于浙江大学医学院，从事临床骨科工作二十余年。现任绍兴市医学会创伤外科专业委员会委员。<br>　　特长：熟练掌握椎间盘疾患，脊柱创伤及退行性疾病的诊断和治疗。', '2018/1/5 14:28:57', '', '33060033060247101420170000274', '274', '0', '', '谢明华', '45', '', ',6,', 'face_274.JPG', '', '颈、腰椎退行性疾病的诊断和手术治疗。', '已在国家级杂志发表多篇论文，参加多项省、市级重点科研项目。', '0', '96', '', '2', '', '', '副主任医师', '1');
