INSERT INTO `department_doctor` VALUES ('103', '2017/12/30 15:18:54', '男，1977年生，汉族。绍兴市立医院曙光优秀青年人才工程计划青年骨干人才。<br>　　专业特长：脑血管病的诊治及二级预防；各种类型的认知功能障碍的诊治。', '2018/1/13 16:01:02', '硕士研究生', '33060033060247101420170000366', '366', '0', '', '韩健', '0', '', ',3,', 'face_366.jpg', '', '脑血管病、帕金森病、头痛、眩晕等基础研究及临床治疗。', '绍兴市卫生计划科技计划青年科技项目1项，撰写了多篇学术论文，在中华系列杂志发表1篇，一级杂志发表2篇，二级杂志3篇，其他2篇。', '0', '99', '', '2', '', '', '副主任医师', '1');
