INSERT INTO `department_doctor` VALUES ('126', '2017/12/30 15:23:24', '2000年毕业于浙江大学医学院临床医学专业，于2011年在北京朝阳医院心脏介入中心进修冠心病急诊介入，2017年赴台湾荣民总医院心血管介入中心进修高端冠心病介入技术。<br>　　专业特长：心血管病介入治疗，特别是冠心病高端精细化介入治疗', '2018/1/15 8:32:12', '硕士', '33060033060247101420170000373', '373', '0', '', '王平', '33', '', ',1,', 'face_373.JPG', '', '急症、重症冠心病高端精细化介入治疗。', '每年独立完成各类心血管介入手术500余例，参编专著10余部，参与省、市级课题10余项，多次荣获省、市科技成果奖励。', '0', '103', '', '2', '', '', '副主任医师', '1');
