INSERT INTO `department_doctor` VALUES ('200', '2017/12/30 14:00:25', '从事泌尿外科临床工作12年，对泌尿泌尿系结石、肿瘤等各种常见病及多发病有丰富诊治经验，曾到浙一医院、上海市第一人民医院进修学习泌尿外科腹腔镜技术，现主要开展泌尿外科各种微创治疗。', '2018/1/15 14:11:56', '博士在读', '33060033060247101420170000285', '285', '0', '', '李俊龙', '42', '', ',,', 'face_285.JPG', '', '', '', '0', '128', '', '2', '', '', '副主任医师', '1');
