INSERT INTO `department_doctor` VALUES ('240', '2015/6/8 9:45:29', '从事临床微生物检验工作20年多年，现担任绍兴市立医院检验科副主任,绍兴市立医院传染病重点实验室副主任,中华医学会微生物与免疫学分会&nbsp;L型细菌学组委员、浙江省防痨协会理事、浙江省防痨协会检验专业委员、浙江省预防医学会微生态专业委员。<br>　　专业特长：临床微生物实验室诊断和实验技术研究。', '2018/1/13 13:32:57', '本科', '33060033060247101420150000219', '219', '0', '', '金法祥', '0', '', ',,', '', '', '病原微生物耐药基因研究。', '主持科研项目7项，《结核杆菌快速培养》等分别获浙江省科技进步三等奖、浙江省医药卫生科技创新二、二等奖、绍兴市科技进步一、二等奖等，参与其他科研项目7项，发表在《中华检验医学杂志》、《中华临床感染病杂志》等中华级杂志16篇，新发现鲍氏不动杆菌2种耐药基因carO新的变异型，已登录美国GenBank基因库。', '0', '113', '', '2', '', '', '主任技师', '1');
