INSERT INTO `department_doctor` VALUES ('247', '2018/1/2 8:55:42', '现担任绍兴市立医院检验科免疫室主任,绍兴市中西医结合学会检验医学专业委员会委员。<br>　　从事临床检验工作二十多年，擅长临床生化、免疫检验技术，对生化免疫流水线、流式细胞仪的使用操作和质量管理积累了丰富的经验。参与多项科研，发表专业论文多篇。', '2018/1/13 13:34:58', '本科', '33060033060247101420180000492', '492', '0', '', '陶叶海', '0', '', ',,', '', '', '', '', '0', '113', '', '2', '', '', '副主任技师', '1');
